## -- Part 2 --

test_str = 'xBxAAABCDxCC'

str = ''

curr = []
total = 0
x_counter = 0

vals = {
    'A': 0,
    'B': 1,
    'C': 3,
    'D': 5
}

x_count_modifier = {
    0: 6,
    1: 2,
    2: 0,
    3: 0
}

for i in range(len(str)):
    enemy = str[i]

    if enemy == 'x':
        x_counter += 1
    else:
        curr.append(vals[enemy])

    if len(curr) + x_counter >= 3:
        total += sum(curr) + x_count_modifier[x_counter]
        curr = []
        x_counter = 0
    
if curr != []:
    x_counter += 3 - len(curr)
    total += sum(curr) + x_count_modifier[x_counter]

print(total)

    

    
